INSERT INTO `aviso_actualizacion` (`id_aviso`, `id_destino`, `titulo_aviso`, `detalle_aviso`, `fecha_publicacion`, `publicado_por`, `estado`) VALUES
(1, 239, 'Estados Unidos exige entrevista presencial para visas', 'A partir de septiembre de 2025, todos los solicitantes de visas de turista y no inmigrantes para EE. UU. deberán acudir personalmente a una entrevista en consulado, eliminando varias exenciones anteriores.', '2026-01-26 10:00:00', 1, 'activo'),
(2, 239, 'Suspensión de emisión de visas para varios países', 'Desde el 1 de enero de 2026, la emisión de ciertas visas estadounidenses se ha suspendido total o parcialmente para ciudadanos de múltiples países como parte de una nueva proclamación migratoria de EE. UU.. Esto puede afectar a colombianos que planean viajes con conexiones o escalas.', '2026-01-26 10:05:00', 1, 'inactivo'),
(3, 83, 'Reino Unido reinstaura requisito de visa para colombianos', 'Desde noviembre de 2024, los ciudadanos colombianos ya no entran al Reino Unido sin visa y deben tramitar el visado correspondiente para visitas o tránsito, terminando la exención anterior.', '2026-01-26 10:10:00', 1, 'activo'),
(4, 70, 'ETIAS obligatorio para entrar al espacio Schengen', 'Colombianos deberán solicitar el sistema ETIAS para viajar a la mayoría de países del espacio Schengen desde 2025/2026, incluso si no se requiere visa tradicional.', '2026-01-26 10:15:00', 1, 'activo'),
(5, 38, 'Acuerdo de exención de visa con Bielorrusia', 'A partir de diciembre de 2025, colombianos pueden ingresar a Bielorrusia sin visa por turismo hasta por 90 días gracias a un nuevo acuerdo bilateral.', '2026-01-26 10:20:00', 1, 'inactivo');
